import React, { useState, useEffect } from 'react';
import '../static/MediaFormStyle.css';  // Import the CSS file

function MediaForm({ mediaItem, onSave }) {
    const [name, setName] = useState('');
    const [type, setType] = useState('');
    const [status, setStatus] = useState('watching');
    const [episodesWatched, setEpisodesWatched] = useState(0);
    const [rating, setRating] = useState('');
    const [review, setReview] = useState('');

    useEffect(() => {
        if (mediaItem) {
            setName(mediaItem.name);
            setType(mediaItem.type);
            setStatus(mediaItem.status);
            setEpisodesWatched(mediaItem.episodes_watched);
            setRating(mediaItem.rating);
            setReview(mediaItem.review);
        }
    }, [mediaItem]);

    const handleSubmit = (e) => {
        e.preventDefault();
        const newMedia = {
            id: mediaItem ? mediaItem.id : undefined,
            name,
            type,
            status,
            episodes_watched: episodesWatched,
            rating,
            review,
        };

        if (mediaItem) {
            onSave(newMedia);
        } else {
            fetch('http://127.0.0.1:8000/api/media', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(newMedia),
            })
                .then(response => response.json())
                .then(() => {
                    setName('');
                    setType('');
                    setStatus('watching');
                    setEpisodesWatched(0);
                    setRating('');
                    setReview('');
                })
                .catch(error => console.error('Error adding media:', error));
        }
    };

    return (
        <div className="media-form-container">
            <h2 className="media-form-title">{mediaItem ? 'Edit Media' : 'Add New Media'}</h2>
            <form className="media-form" onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Name:</label>
                    <input type="text" value={name} onChange={(e) => setName(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Type:</label>
                    <input type="text" value={type} onChange={(e) => setType(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Status:</label>
                    <select value={status} onChange={(e) => setStatus(e.target.value)}>
                        <option value="watching">Watching</option>
                        <option value="completed">Completed</option>
                        <option value="dropped">Dropped</option>
                    </select>
                </div>
                <div className="form-group">
                    <label>Episodes Watched:</label>
                    <input type="number" value={episodesWatched} onChange={(e) => setEpisodesWatched(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Rating:</label>
                    <input type="text" value={rating} onChange={(e) => setRating(e.target.value)} />
                </div>
                <div className="form-group">
                    <label>Review:</label>
                    <textarea value={review} onChange={(e) => setReview(e.target.value)} />
                </div>
                <button className="submit-button" type="submit">{mediaItem ? 'Save Changes' : 'Add Media'}</button>
            </form>
        </div>
    );
}

export default MediaForm;
