import React, { useEffect, useState } from 'react';
import MediaForm from './MediaForm';

function MediaList() {
    const [media, setMedia] = useState([]);
    const [editingItem, setEditingItem] = useState(null);

    useEffect(() => {
        fetch('http://127.0.0.1:5000/api/media')
            .then(response => response.json())
            .then(data => setMedia(data))
            .catch(error => console.error('Error fetching media:', error));
    }, []);

    const handleDelete = (id) => {
        fetch(`http://127.0.0.1:5000/api/media/${id}`, {
            method: 'DELETE',
        })
            .then(response => response.json())
            .then(() => setMedia(media.filter(item => item.id !== id)))
            .catch(error => console.error('Error deleting media:', error));
    };

    const handleEdit = (item) => {
        setEditingItem(item);
    };

    const handleUpdate = (updatedItem) => {
        fetch(`http://127.0.0.1:5000/api/media/${updatedItem.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedItem),
        })
            .then(response => response.json())
            .then(() => {
                setMedia(media.map(item => item.id === updatedItem.id ? updatedItem : item));
                setEditingItem(null); // Reset the editing state
            })
            .catch(error => console.error('Error updating media:', error));
    };

    return (
        <div>
            <h2>All Media</h2>
            {editingItem ? (
                <MediaForm mediaItem={editingItem} onSave={handleUpdate} />
            ) : (
                <ul>
                    {media.map(item => (
                        <li key={item.id}>
                            <span>{item.name} ({item.type}) - {item.status} - {item.episodes_watched} episodes watched - Rating: {item.rating} - {item.review}</span>
                            <button onClick={() => handleDelete(item.id)}>Delete</button>
                            <button onClick={() => handleEdit(item)}>Edit</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default MediaList;
