// components/MediaDetail.js
import React, { useState, useEffect } from 'react';
import { useParams, useLocation } from 'react-router-dom';
import '../static/MediaDetailStyle.css'; // CSS for styling

function MediaDetail({ setRefresh }) {
    const { source, id } = useParams();
    const location = useLocation();
    const [mediaType, setMediaType] = useState('');
    const [mediaInfo, setMediaInfo] = useState(null);

    useEffect(() => {
        const params = new URLSearchParams(location.search);
        const type = params.get('media_type');
        setMediaType(type);

        let url = `http://127.0.0.1:8000/api/media_info?source=${source}&id=${id}`;
        if (source === 'tmdb') {
            url += `&media_type=${type}`;
        }

        fetch(url)
            .then(response => response.json())
            .then(data => {
                setMediaInfo(data);
            })
            .catch(error => console.error('Error fetching media info:', error));
    }, [source, id, location.search]);

    const handleAddToList = () => {
        // Prepare data to add to user's list
        const newMedia = {
            name: mediaInfo.title,
            type: mediaType,
            status: 'watching', // Default status
            episodes_watched: 0,
            rating: 0,
            review: '',
        };

        fetch('http://127.0.0.1:8000/api/media', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(newMedia),
        })
            .then(response => response.json())
            .then(() => {
                alert('Media added to your list!');
                if (setRefresh) setRefresh(prev => !prev); // Toggle refresh state
            })
            .catch(error => console.error('Error adding media:', error));
    };

    if (!mediaInfo) {
        return <p>Loading media details...</p>;
    }

    return (
        <div className="media-detail-container">
            <h2 className="media-detail-title">{mediaInfo.title}</h2>
            <img src={mediaInfo.image_url} alt={mediaInfo.title} />
            <p><strong>Type:</strong> {mediaInfo.type}</p>
            <p><strong>Episodes:</strong> {mediaInfo.episodes || 'N/A'}</p>
            <p><strong>Score:</strong> {mediaInfo.score || 'N/A'}</p>
            <p><strong>Status:</strong> {mediaInfo.status || 'N/A'}</p>
            <p><strong>Genres:</strong> {mediaInfo.genres ? mediaInfo.genres.join(', ') : 'N/A'}</p>
            <p><strong>Synopsis:</strong></p>
            <p>{mediaInfo.synopsis}</p>
            <button onClick={handleAddToList} className="add-to-list-button">Add to My List</button>
        </div>
    );
}

export default MediaDetail;
