import React, { useState } from 'react';

function AddMediaForm({ onMediaAdded }) {
    const [name, setName] = useState('');
    const [type, setType] = useState('');
    const [status, setStatus] = useState('watching');
    const [episodesWatched, setEpisodesWatched] = useState(0);
    const [rating, setRating] = useState('');
    const [review, setReview] = useState('');

    const handleSubmit = (e) => {
        e.preventDefault();
        const newMedia = { name, type, status, episodes_watched: episodesWatched, rating, review };

        fetch('http://127.0.0.1:5000/api/media', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(newMedia),
        })
            .then(response => response.json())
            .then(() => {
                onMediaAdded(); // Refresh the media list
                setName('');
                setType('');
                setStatus('watching');
                setEpisodesWatched(0);
                setRating('');
                setReview('');
            })
            .catch(error => console.error('Error adding media:', error));
    };

    return (
        <div>
            <h2>Add New Media</h2>
            <form onSubmit={handleSubmit}>
                <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Name" required />
                <input type="text" value={type} onChange={(e) => setType(e.target.value)} placeholder="Type" required />
                <select value={status} onChange={(e) => setStatus(e.target.value)}>
                    <option value="watching">Watching</option>
                    <option value="completed">Completed</option>
                    <option value="dropped">Dropped</option>
                </select>
                <input type="number" value={episodesWatched} onChange={(e) => setEpisodesWatched(Number(e.target.value))} placeholder="Episodes Watched" />
                <input type="number" step="0.1" value={rating} onChange={(e) => setRating(e.target.value)} placeholder="Rating" />
                <textarea value={review} onChange={(e) => setReview(e.target.value)} placeholder="Review"></textarea>
                <button type="submit">Add Media</button>
            </form>
        </div>
    );
}

export default AddMediaForm;
