import React, { useState, useEffect } from 'react';
import MediaForm from './MediaForm'; // Import your MediaForm component

const MediaList = () => {
    const [media, setMedia] = useState([]);
    const [editingItem, setEditingItem] = useState(null);

    useEffect(() => {
        fetch('http://127.0.0.1:8000/api/media')
            .then(response => response.json())
            .then(data => {

                const transformedData = data.map(item => ({
                    id: item[0],
                    name: item[1],
                    type: item[2],
                    status: item[3],
                    episodes_watched: item[4],
                    rating: item[5],
                    review: item[6]
                }));
                setMedia(transformedData);
            })
            .catch(error => console.error('Error fetching media:', error));
    }, []);

    // Handle deleting a media item
    const handleDelete = (id) => {
        fetch(`http://127.0.0.1:8000/api/media/${id}`, {
            method: 'DELETE',
            headers: {
                'Content-Type': 'application/json'
            }
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(() => {
                setMedia(media.filter(item => item.id !== id));
            })
            .catch(error => console.error('Error deleting media:', error));
    };


    // Handle starting edit mode
    const handleEdit = (item) => {
        setEditingItem(item);
    };

    // Handle updating a media item
    const handleUpdate = (updatedItem) => {
        fetch(`http://127.0.0.1:5000/api/media/${updatedItem.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify(updatedItem),
        })
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then(data => {
                // Update the local media state with the updated item
                setMedia(media.map(item =>
                    item.id === data.id ? data : item
                ));
                setEditingItem(null);
            })
            .catch(error => console.error('Error updating media:', error));
    };


    return (
        <div>
            <h2>All Media</h2>
            {editingItem ? (
                <MediaForm mediaItem={editingItem} onSave={handleUpdate} />
            ) : (
                <ul>
                    {media.map(item => (
                        <li key={item.id}>
                            <span>{item.name} ({item.type}) - {item.status} - {item.episodes_watched} episodes watched - Rating: {item.rating} - {item.review}</span>
                            <button onClick={() => handleDelete(item.id)}>Delete</button>
                            <button onClick={() => handleEdit(item)}>Edit</button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default MediaList;
