// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import MediaList from './components/MediaList';
import SearchBar from './components/SearchBar';
import SearchResults from './components/SearchResults';
import MediaDetail from './components/MediaDetail';
import './static/style.css'; // Import the CSS file

function App() {
  const [refresh, setRefresh] = useState(false);

  return (
    <Router>
      <div className="app-container">
        <h1 className="app-title">Media Tracker</h1>
        <SearchBar />
        <Routes>
          <Route
            path="/"
            element={
              <>
                {/* Removed the AddMediaForm component */}
                <MediaList refresh={refresh} setRefresh={setRefresh} />
              </>
            }
          />
          <Route path="/search" element={<SearchResults />} />
          <Route path="/media/:source/:id" element={<MediaDetail setRefresh={setRefresh} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
