// components/SearchResults.js
import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import SearchBar from './SearchBar'; // Import the SearchBar component
import '../static/SearchResultsStyle.css'; // CSS for styling

function SearchResults() {
    const [results, setResults] = useState([]);
    const location = useLocation();

    // Extract query parameter
    const query = new URLSearchParams(location.search).get('q');

    useEffect(() => {
        if (query) {
            fetch(`http://127.0.0.1:8000/api/search?q=${encodeURIComponent(query)}`)
                .then(response => response.json())
                .then(data => {
                    setResults(data);
                })
                .catch(error => console.error('Error fetching search results:', error));
        }
    }, [query]);

    return (
        <div className="search-results-container">
            <SearchBar /> {/* Add the SearchBar component here */}
            {query && <h2 className="search-results-title">Search Results for "{query}"</h2>}
            {results.length > 0 ? (
                <ul className="search-results-list">
                    {results.map((item) => (
                        <li key={`${item.source}-${item.id}`} className="search-result-item">
                            <img src={item.image_url} alt={item.title} />
                            <div className="result-details">
                                <h3>{item.title}</h3>
                                <p>{item.synopsis}</p>
                                <Link
                                    to={`/media/${item.source}/${item.id}?media_type=${item.type.toLowerCase()}`}
                                >
                                    View Details
                                </Link>
                            </div>
                        </li>
                    ))}
                </ul>
            ) : (
                query && <p>No results found.</p>
            )}
        </div>
    );
}

export default SearchResults;
